// Combine all categories
export const CS2_ITEMS_DATABASE: CS2Item[] = [
  ...KNIVES,
  ...RIFLES,
  ...PISTOLS,
  ...SMGS,
  ...HEAVY,
];

// Category helpers
export const ITEMS_BY_CATEGORY = {
  knife: KNIVES,
  rifle: RIFLES,
  pistol: PISTOLS,
  smg: SMGS,
  heavy: HEAVY,
};

// Search function
export function searchCS2Items(query: string, limit = 50): CS2Item[] {
  const lowerQuery = query.toLowerCase();
  return CS2_ITEMS_DATABASE
    .filter(item => 
      item.name.toLowerCase().includes(lowerQuery) ||
      item.weapon?.toLowerCase().includes(lowerQuery)
    )
    .slice(0, limit);
}

// Get items by weapon type
export function getItemsByWeapon(weapon: string): CS2Item[] {
  return CS2_ITEMS_DATABASE.filter(item => 
    item.weapon?.toLowerCase() === weapon.toLowerCase()
  );
}
